CREATE TABLE IF NOT EXISTS `tokens` (    `user_id` int(11) NOT NULL,    `hashedtoken` varchar(200) PRIMARY KEY,    `expires` DATETIME NOT NULL DEFAULT current_timestamp() ON UPDATE current_timestamp(),    FOREIGN KEY (user_id) REFERENCES usuarios(id) ON DELETE CASCADE) ENGINE = InnoDB DEFAULT CHARSET = utf8mb4 COMMENT = 'tabla de tokens';
CREATE TABLE IF NOT EXISTS `fondeos` (  `nif` varchar(8) NOT NULL,  `calado` decimal(5, 2) UNSIGNED DEFAULT NULL,  `longitud_cadena` decimal(5, 2) UNSIGNED DEFAULT NULL,  `ubicacion` ENUM('exterior', 'intermedio', 'interior') default 'interior',  `h_muerto` smallint unsigned DEFAULT NULL,  `b_muerto` smallint unsigned DEFAULT NULL,  `l_muerto` smallint unsigned DEFAULT NULL,  `diametro_cadena` TINYINT unsigned DEFAULT NULL,  `area_total_viva` decimal(5, 3) UNSIGNED DEFAULT NULL,  `Cw_aerodinamico` decimal(5, 4) UNSIGNED DEFAULT NULL,  `area_total_muerta` decimal(5, 3) UNSIGNED DEFAULT NULL,  `Cd_aerodinamico` decimal(5, 4) UNSIGNED DEFAULT NULL,  `observaciones` varchar(250) default null,  `last_modified` DATETIME NOT NULL DEFAULT current_timestamp() ON UPDATE current_timestamp(),  FOREIGN KEY (nif) REFERENCES balizamiento(nif) ON DELETE CASCADE) ENGINE = InnoDB DEFAULT CHARSET = utf8mb4 COMMENT = 'tabla de fondeos';